  tem_relatorio: /(?:^|\n)\s*(?:I\s*[-‚Äì.]\s*|1[\.)\s]\s*)?RELAT[√ìO]RIO/im.test(normalizedMinuta),
  tem_fundamentacao: /(?:^|\n)\s*(?:II\s*[-‚Äì.]\s*|2[\.)\s]\s*)?FUNDAMENTA[√áC][√ÉA]O/im.test(normalizedMinuta),
  tem_dispositivo: /(?:^|\n)\s*(?:III\s*[-‚Äì.]\s*|3[\.)\s]\s*)?DISPOSITIVO/im.test(normalizedMinuta),

  // Dispositivo completo
  tem_julgamento: /JULGO\s+(PROCEDENTE|IMPROCEDENTE|PARCIALMENTE\s+PROCEDENTE|EXTINTO)/i.test(normalizedMinuta),
  tem_fundamento_legal: /art(?:igo)?\.?\s*\d+|lei\s*(?:n[¬∞¬∫]?)?\s*[\d.]+/i.test(normalizedMinuta),

  // Sucumb√™ncia
  tem_honorarios: /honor[√°a]rios|art\.?\s*85/i.test(normalizedMinuta),
  tem_custas: /custas/i.test(normalizedMinuta),

  // Qualidade
  tamanho_minimo: normalizedMinuta.split(/\s+/).length >= 150,
  tamanho_maximo: normalizedMinuta.split(/\s+/).length <= 3500,
  sem_placeholders: !/\[(?:NOME|DATA|VALOR|N√öMERO|XXX|INSERIR)\]/i.test(normalizedMinuta)
