// Contar marcadores de revis√£o
const marcadoresRevisar = (normalizedMinuta.match(/\[REVISAR[:\s][^\]]+\]/gi) || []).length;

// ============================================================================
// C√ÅLCULO DO SCORE ESTRUTURAL
