  !validacoes.tem_fundamentacao && 'FUNDAMENTA√á√ÉO ausente',
  !validacoes.tem_dispositivo && 'DISPOSITIVO ausente',
  !validacoes.tem_julgamento && 'Julgamento n√£o declarado'
].filter(Boolean);

// ============================================================================
// OUTPUT
