// Use $input.first() which always works regardless of which branch we came from
const inputData = $input.first().json;

// Check if this is Gemini response (has candidates) or estrutural data (has qa_estrutural)
let estruturalData, geminiData, semantico;

if (inputData.candidates) {
  // Came from QA Sem√¢ntico - Gemini path
  // Need to get estrutural data from the IF node
  geminiData = inputData;
  estruturalData = $('IF: Executar QA Sem√¢ntico?').item?.json || $('QA Estrutural').item?.json;
} else if (inputData.qa_estrutural) {
  // Came from skip semantic path (direct from IF)
  estruturalData = inputData;
  geminiData = null;
} else {
  // Fallback - try to get from previous nodes
  estruturalData = inputData;
  geminiData = null;
