// Safely extract data with fallbacks
const minuta = estruturalData?.minuta || '';
const ctx = estruturalData?.context || {};
const estrutural = estruturalData?.qa_estrutural || { score: 50, falhas_criticas: [], marcadores_revisar: 0, palavras: 0 };
const agente = estruturalData?.agente || 'agent_generico';

// Parsear resposta do Gemini (se executou)
semantico = null;
if (geminiData?.candidates?.[0]) {
  try {
    const candidate = geminiData.candidates[0];
    const finishReason = candidate.finishReason;

    if (finishReason === 'STOP' || finishReason === 'END_TURN' || !finishReason) {
      const text = candidate.content?.parts?.[0]?.text;
      if (text) {
        const cleanJson = text.replace(/```json\n?|```\n?/g, '').trim();
        semantico = JSON.parse(cleanJson);
      }
    } else {
      console.log('QA Sem√¢ntico: finishReason =', finishReason);
