} catch (e) {
  tentativaAtual = 1;
}

const MAX_TENTATIVAS = 3;
const shouldRetry = tentativaAtual < MAX_TENTATIVAS;

// ============================================================================
// LOG DO ERRO
// ============================================================================

const errorLog = {
  timestamp: new Date().toISOString(),
  workflow_id: workflowId,
  error: {
    message: errorMessage,
    node: errorNode,
    tentativa: tentativaAtual,
    max_tentativas: MAX_TENTATIVAS
  },
  action: shouldRetry ? 'RETRY' : 'FAIL',
  next_tentativa: shouldRetry ? tentativaAtual + 1 : null
};

console.log('Error Handler:', JSON.stringify(errorLog));

// ============================================================================
// DECIS√ÉO: RETRY OU FAIL
// ============================================================================

if (shouldRetry) {
  // Sinalizar para retry
  return [{
    json: {
      should_retry: true,
      tentativa: tentativaAtual + 1,
      error_log: errorLog,
      original_error: errorMessage,
      failed_node: errorNode
    }
  }];
} else {
  // Falha final
  return [{
    json: {
      should_retry: false,
      tentativa: tentativaAtual,
      error_log: errorLog,
      original_error: errorMessage,
      failed_node: errorNode,
      final_failure: true
    }
  }];
}

