// Format RAG results for agent context
const results = $input.first().json.result || [];

if (results.length === 0) {
  return [{ json: { rag_context: 'Nenhum precedente relevante encontrado.' } }];
}

const formatted = results.map((r, i) => {
  const p = r.payload;
  let text = `[${i + 1}] `;

  if (p.tipo === 'sumula') {
    text += `Sumula ${p.numero}/${p.tribunal}: ${p.text}`;
  } else if (p.tipo === 'tema_repetitivo') {
    text += `Tema ${p.numero}/${p.tribunal}: ${p.text}`;
  } else {
    text += p.text;
  }

  return text;
}).join('\n\n');

const context = `## PRECEDENTES STJ RELEVANTES\n\n${formatted}\n\n---\nFonte: Vector Store STJ (score >= 0.7)`;

return [{ json: { rag_context: context, results_count: results.length } }];
